const getDaysInMonth = (month, year) => {
    const date = new Date(year, month, 1);
    let days = [];
    while (date.getMonth() === month) {
        days.push(new Date(date));
        date.setDate(date.getDate() + 1);
    }
    return days;
}

export {
    getDaysInMonth
}
